import json import os from typing import List, Tuple
from tqdm import tqdm
from model import Job, SearchTerm from sites.indeed import Indeed from sites.jora import Jora from sites.linkedin import LinkedIn from sites.seek import Seek from sites.site import JobType, Site
def main(): # Fetch search terms from the database search_terms = [st.term for st in SearchTerm.select()] sites = [Indeed(), Jora(), LinkedIn(), Seek()]
for site in tqdm(sites, desc="Sites", unit="site"): for query in tqdm(search_terms, desc="Terms", unit="term", leave=False): site.download_new_listings(query)
# easy_filter(blacklist_terms)
def easy_filter(blacklist_terms: List[str]): new_jobs = Job.select().where(Job.status == "new") for term in blacklist_terms: for job in new_jobs: if term.lower() in job.title.lower(): job.status = "easy_filter" job.save()
def load_config() -> Tuple[List[str], List[str], List[Site], List[JobType]]: root_path = os.path.realpath(__file__)[: os.path.realpath(__file__).rindex("Job-Search") + 10] with open(f"{root_path}/config/config.json", "r") as f: config = json.load(f)
sites = [] for site in config["sites"]: match site: case "indeed": sites.append(Indeed()) case "jora": sites.append(Jora()) case "seek": sites.append(Seek()) case "linkedin": sites.append(LinkedIn())
types = [] for type in config["types"]: match type: case "full": types.append(JobType.FULL) case "part": types.append(JobType.PART) case "casual": types.append(JobType.CASUAL)
return config["search-terms"], config["title-blacklist"], sites, types
if __name__ == "__main__": main()

import json import os from typing import List, Tuple
from tqdm import tqdm
from model import Job, SearchTerm from sites.indeed import Indeed from sites.jora import Jora from sites.linkedin import LinkedIn from sites.seek import Seek from sites.site import JobType, Site
def main(): # Fetch search terms from the database search_terms = [st.term for st in SearchTerm.select()] sites = [Indeed(), Jora(), LinkedIn(), Seek()]
for site in tqdm(sites, desc="Sites", unit="site"): for query in tqdm(search_terms, desc="Terms", unit="term", leave=False): site.download_new_listings(query)
# easy_filter(blacklist_terms)
def easy_filter(blacklist_terms: List[str]): new_jobs = Job.select().where(Job.status == "new") for term in blacklist_terms: for job in new_jobs: if term.lower() in job.title.lower(): job.status = "easy_filter" job.save()
def load_config() -> Tuple[List[str], List[str], List[Site], List[JobType]]: root_path = os.path.realpath(__file__)[: os.path.realpath(__file__).rindex("Job-Search") + 10] with open(f"{root_path}/config/config.json", "r") as f: config = json.load(f)
sites = [] for site in config["sites"]: match site: case "indeed": sites.append(Indeed()) case "jora": sites.append(Jora()) case "seek": sites.append(Seek()) case "linkedin": sites.append(LinkedIn())
types = [] for type in config["types"]: match type: case "full": types.append(JobType.FULL) case "part": types.append(JobType.PART) case "casual": types.append(JobType.CASUAL)
return config["search-terms"], config["title-blacklist"], sites, types
if __name__ == "__main__": main()

from peewee import ( Model, AutoField, CharField, ForeignKeyField, IntegerField, CompositeKey, TextField, PostgresqlDatabase, )
db = PostgresqlDatabase("job_search", user="josh", password="password", host="monitoring.lan") db.connect()
class Job(Model): id = AutoField(primary_key=True) title = TextField() company = TextField() status = CharField(default="new") username = CharField() # New field for multi-user support
class Meta: database = db
class Listing(Model): id = CharField(primary_key=True) site = CharField() summary = TextField()
class Meta: database = db
class JobToListing(Model): job_id = ForeignKeyField(Job) listing_id = ForeignKeyField(Listing)
class Meta: database = db
class PageCount(Model): site = CharField() query = CharField() pages = IntegerField(default=1)
class Meta: database = db primary_key = CompositeKey("site", "query")
class SearchTerm(Model): id = AutoField(primary_key=True) term = CharField(unique=True)
class Meta: database = db
class User(Model): id = AutoField(primary_key=True) username = CharField(unique=True)
class Meta: database = db
db.create_tables([Job, Listing, JobToListing, PageCount, SearchTerm, User], safe=True)

from peewee import ( Model, AutoField, CharField, ForeignKeyField, IntegerField, CompositeKey, TextField, PostgresqlDatabase, )
db = PostgresqlDatabase("job_search", user="josh", password="password", host="monitoring.lan") db.connect()
class Job(Model): id = AutoField(primary_key=True) title = TextField() company = TextField() status = CharField(default="new") username = CharField() # New field for multi-user support
class Meta: database = db
class Listing(Model): id = CharField(primary_key=True) site = CharField() summary = TextField()
class Meta: database = db
class JobToListing(Model): job_id = ForeignKeyField(Job) listing_id = ForeignKeyField(Listing)
class Meta: database = db
class PageCount(Model): site = CharField() query = CharField() pages = IntegerField(default=1)
class Meta: database = db primary_key = CompositeKey("site", "query")
class SearchTerm(Model): id = AutoField(primary_key=True) term = CharField(unique=True)
class Meta: database = db
class User(Model): id = AutoField(primary_key=True) username = CharField(unique=True)
class Meta: database = db
db.create_tables([Job, Listing, JobToListing, PageCount, SearchTerm, User], safe=True)

