        <h3 class="card-subtitle">Summary</h3>
        <div class="card-text">{{ listing.listing_id.summary }}</div>
        <div class="card-text">Source: {{ listing.listing_id.site|capitalize }}</div>
    </div>
    {% endfor %}

    <div class="job-links">
        <h3 class="card-subtitle">Open Job Listings</h3>
        <div class="action-buttons">
            {% for site_name, site_url in sites %}
            <a href="{{ site_url }}" target="_blank" class="btn btn-primary">
                {{ site_name }}
            </a>
            {% endfor %}
        </div>
    </div>

    <form action="{{ url_for('reading_action') }}" method="post">
        <input type="hidden" name="job_id" value="{{ job.id }}">
        <div class="action-buttons">
            <button type="submit" name="action" value="liked" class="btn btn-success">
                Apply for Position
            </button>
            <button type="submit" name="action" value="not_interested" class="btn btn-danger">
                Not a Good Fit
            </button>
        </div>
    </form>
</div>
